insert into product (`id`, `product_name`, `description`, `price`, `purchase_count`, `rate_avg`, `limit_count`, `total_count`, `product_status`, `title_img`, `large_category`, `shipping_cost`, `shipping_due_date`, `sale_rate`) values (0, 'Untitled (Rothko)', 'Untitled is a painting by the Russian-American Abstract expressionist artist Mark Rothko. It was painted in 1952. In common with Rothko''s other works from this period, it consists of large expanses of colour delineated by uneven, hazy shades.In 2014, Untitled was bought for $66 million by an anonymous buyer.', 66,1,3,1,1,1, 'https://www.jnilbo.com/photos/2021/01/23/2021012320102064134_l.jpg', 'Mark Rothko', 0, 3, 5);
insert into product (`id`, `product_name`, `description`, `price`, `purchase_count`, `rate_avg`, `limit_count`, `total_count`, `product_status`, `title_img`, `large_category`, `shipping_cost`, `shipping_due_date`, `sale_rate`) values (1, 'Orange and Yellow', 'Orange and Yellow reflects Mark Rothko’s mature style, in which two or three rectangles are set within a background that surrounds them all, but divides them gently from one another. The edges of the rectangles are never distinct, avoiding an optical break and allowing viewers’ eyes to move quietly from other area to another in a contemplative way.', 77,1060,4,1,1,1, 'https://uploads2.wikiart.org/images/mark-rothko/orange-and-yellow(1).jpg', 'Mark Rothko', 2, 1, 25);
insert into product (`id`, `product_name`, `description`, `price`, `purchase_count`, `rate_avg`, `limit_count`, `total_count`, `product_status`, `title_img`, `large_category`, `shipping_cost`, `shipping_due_date`, `sale_rate`) values (2, 'Multiform', 'Multiform (1948) belongs to a group of paintings by Mark Rothko, which signifies the transition toward his mature abstract style. These paintings, collectively known as ''Multiforms'', were painted between 1947 and 1949. The artist did not officially use the name ''Multiforms''; the term was adopted by art critics and historians to describe the group of paintings. In the specific case of Multiform, it seems the title was only used after Rothko''s death in 1970. It first appeared in the catalog for the Rothko exhibition held at the 1970 Venice Biennale. The catalog was put together by the staff of the Marlborough Gallery, who adopted the term ''Multiforms'', claiming it was casually used by Rothko to describe this group of artworks.', 102,1,5,1,1,1, 'https://uploads6.wikiart.org/images/mark-rothko/multiform-1948.jpg!Large.jpg', 'Mark Rothko', 2, 4, 40);
